'use strict';

var grunt = require('grunt');

/*
  ======== A Handy Little Nodeunit Reference ========
  https://github.com/caolan/nodeunit

  Test methods:
    test.expect(numAssertions)
    test.done()
  Test assertions:
    test.ok(value, [message])
    test.equal(actual, expected, [message])
    test.notEqual(actual, expected, [message])
    test.deepEqual(actual, expected, [message])
    test.notDeepEqual(actual, expected, [message])
    test.strictEqual(actual, expected, [message])
    test.notStrictEqual(actual, expected, [message])
    test.throws(block, [error], [message])
    test.doesNotThrow(block, [error], [message])
    test.ifError(value)
*/

exports.php_constants = {

  setUp: function(done) {
    done();
  },

  static_option: function(test) {
    test.expect(1);

    var filename = 'static.php';

    var actual = grunt.file.read('tmp/' + filename);
    var expected = grunt.file.read('test/expected/' + filename);
    test.equal(actual, expected, 'Should be equal.');

    test.done();
  },

  template_option: function(test) {
    test.expect(1);

    var filename = 'template.php';

    var actual = grunt.file.read('tmp/' + filename);
    var expected = grunt.file.read('test/expected/' + filename);
    test.equal(actual, expected, 'Should be equal.');

    test.done();
  },

  function_option: function(test) {
    test.expect(1);

    var filename = 'function.php';

    var actual = grunt.file.read('tmp/' + filename);
    var expected = grunt.file.read('test/expected/' + filename);
    test.equal(actual, expected, 'Should be equal.');

    test.done();
  }

};
